import { useState, useEffect } from "react";
import { useUserContext } from "../../Context/UserContext";
import "./Question.css";
import { Link, useNavigate } from "react-router-dom";
import axios from "../../Constant/axios";

function Question() {
  const [userData] = useUserContext();
  const navigate = useNavigate();
  const [form, setForm] = useState({
    title: "",       // Initialize with an empty string for title
    question: "",    // Initialize with an empty string for question
  });

  useEffect(() => {
    if (!userData.user) navigate("/login");
  }, [userData.user, navigate]);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      // Sending user data to the database to be logged in
      const questionAddRes = await axios.post("api/question/add", {
        question: form.title,
        question_description: form.question,
        question_code_block: "....",
        tags: "...",
        user_id: userData.user.id,
      });

      alert(questionAddRes.data.msg);
      // Navigate user to the homepage
      navigate("/");
    } catch (err) {
      console.log("problem", err.response.data.msg);
      alert(err.response.data.msg);
    }
  };

  return (
    <div className="question flex align-center justify-center">
      <div className="question__description">
        <h1 className="mb-1 col-1">Steps to write a good question</h1>
        <ul>
          <li>Summarize your problem in a one-line title.</li>
          <li>Describe your problem in more detail.</li>
          <li>Describe what you tried and what you expected to happen.</li>
          <li>Review your question and post it to the site.</li>
        </ul>
      </div>
      <div className="question__form">
        <h1 className="mb-1 col-1">Ask a public question</h1>
        <Link to="/">Go to Question page</Link>
        <form onSubmit={handleSubmit}>
          <input
            className="input"
            type="text"
            name="title"
            onChange={handleChange}
            placeholder="Question Title"
          />
          <br />
          <textarea
            className="textarea"
            name="question"
            onChange={handleChange}
            cols="60"
            rows="10"
            placeholder="Question Description..."
          ></textarea>
          <br />
          <button
            className="btn btn-1 bg-2"
            disabled={form.title && form.question ? false : true}
          >
            Post Your Question
          </button>
        </form>
      </div>
    </div>
  );
}

export default Question;
