import React, { useContext, useEffect, useState } from "react";
import prof from "../imgs.png";
import "./ListQuestion.css";
import ArrowForwardIosOutlinedIcon from "@mui/icons-material/ArrowForwardIosOutlined";
import { Link, useNavigate } from "react-router-dom";
import { UserContext } from "../../context/UseContext";
import AskQuestion from "../AskQuestion/AskQuestion";
import axios from "axios";

function ListQuestion() {
  const [userData, setUserData] = useContext(UserContext);
  const navigate = useNavigate();
  const [question, setQuestion] = useState();
  useEffect(() => {
    const fetchData = async () => {
      try {
        const data = await axios.get("http://localhost:4000/api/question");
        console.log(data.data);
        setQuestion(data.data.data);
      } catch (err) {
        console.log(err);
      }
    };
    fetchData();
  }, []);

  // const fetchData = async () => {
  //   try {
  //     const response = await fetch("http://localhost:5000/api/question");
  //     const data = await response.json();
  //     setQuestion(data.data);
  //   } catch (err) {
  //     //       console.log("problem", err);
  //   }
  // };
  // useEffect(() => {
  //   fetchData();
  // }, []);
  // console.log(question);
  useEffect(() => {
    if (!userData.user) navigate("/");
  }, [userData.user, navigate]);

  return (
    <div className="maincontainer">
      <div className="NameandLink">
        <p>
          <Link to={"/AskQuestion"}>Ask Question</Link>
        </p>
        <p>Wellcome:{userData.user?.display_name}</p>
      </div>
      <div className="ListQuetion">
        <h3>Question</h3>
      </div>

      {question?.map((question, i) => {
        var Q_id = question.question_id.toString();
        console.log(Q_id);

        return (
          <Link className="linkspropertu" to={Q_id} key={i}>
            <div className="userprof">
              <div className="userprof1">
                <div className="usersprofile">
                  <img src={prof} />
                  <p className="userName">{question.user_name}</p>
                </div>
                <div className="userans">
                  <p>{question.description}</p>
                </div>
              </div>
              <div>
                <ArrowForwardIosOutlinedIcon className="arrows" />
              </div>
            </div>
          </Link>
        );
      })}
    </div>
  );
}

export default ListQuestion;
