import axios from "axios";
import React, { useContext, useEffect, useState } from "react";
import { useNavigate } from "react-router-dom";
import { UserContext } from "../../context/UseContext";
import "./Login.css";

function Login() {
  const [userData, setUserData] = useContext(UserContext);
  const [err, setError] = useState(null);
  const navigate = useNavigate();
  const [form, setForm] = useState({});
  const [passwordShown, setPasswordShown] = useState(false);

  const handleChange = (e) => {
    setForm({ ...form, [e.target.name]: e.target.value });
  };

  const togglePassword = () => {
    setPasswordShown(!passwordShown);
  };

  const handleSubmitsign = async (e) => {
    e.preventDefault();

    const requiredFields = [
      "email",
      "firstName",
      "lastName",
      "userName",
      "password",
    ];

    const isValid = requiredFields.every((field) => {
      return form[field] !== undefined && form[field].trim().length > 0;
    });

    if (!isValid) {
      setError("Please fill out all of the required fields");
      return;
    }

    const user = {
      email: form.email,
      password: form.password,
    };

    try {

      await axios.post("http://localhost:4000/api/users/signin", user);
      await handleSubmit(e);
    } catch (error) {
      if (error.response && error.response.status === 401) {
        setError("Unauthorized access");
      } else {
        console.log(error);
        setError("An error occurred");
      }
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    try {
      const loginRes = await axios.post("http://localhost:4000/api/users", {
        email: form.email,
        password: form.password,
      });

      setUserData({
        token: loginRes.data.token,
        user: loginRes.data.user,
      });

      localStorage.setItem("auth-token", loginRes.data.token);

      navigate("/ListQuestion");
    } catch (error) {
      console.log("problem", error.response.data.msg);
      setError(error?.response.data.msg);
    }
  };

  useEffect(() => {
    if (userData.user) navigate("/ListQuestion");
  }, [userData.user, navigate]);

  return (
    <div className="home">
      {/* signin part */}
      <div className="form-wraper hide" id="create">
        <div className="describtion">
          <h4>Join the Network</h4>
          <p>
            Already have an account?
            <div className="linksto">
              <a href="#">Sign In</a>
            </div>
          </p>
        </div>
        <form onSubmit={handleSubmitsign}>
          <div className="mb-3 ">
            <input
              className="inpts"
              type="email"
              name="email"
              onChange={handleChange}
              placeholder="Your Email"
            />
          </div>
          <div className="mb-3">
            <div className="inputwrap">
              <input
                className="inpts2"
                type="text"
                name="firstName"
                placeholder="First Name"
                onChange={handleChange}
              />
              <input
                className="inpts1"
                type="text"
                name="lastName"
                placeholder="Last Name"
                onChange={handleChange}
              />
            </div>
            <div className="mb-3 ">
              <input
                className="inpts"
                type="text"
                name="userName"
                placeholder="User Name"
                onChange={handleChange}
              />
            </div>

            <input
              type={passwordShown ? "text" : "password"}
              placeholder="Your Password"
              name="password"
              onChange={handleChange}
            />

            <i
              className={passwordShown ? " fa fa-eye " : "fa fa-eye-slash"}
              aria-hidden="true"
              id="togglePassword"
              onClick={togglePassword}
            ></i>
          </div>
          <div className="d-grid">
            <button
              className="btn btn-primary btn-login login-button col-md-12"
              type="submit"
            >
              Agree and join
            </button>
            {err && <p className="errror">{err}</p>}
          </div>
        </form>

        <div>
          <div className="creat-link">
            {" "}
            <a href="#">Already have account?</a>
          </div>
        </div>
      </div>

      {/* login part */}
      <div className="form-wraper" id="login">
        <div className="describtion">
          <h4>Login to your account</h4>
          <p>
            Don’t have an account?
            {"  "}
            <div className="linksto">
              <a href="#">Create a new account </a>
            </div>
          </p>
        </div>
        <form onSubmit={handleSubmit}>
          <div className="mb-3 ">
            <input
              className="inpts"
              name="email"
              type="text"
              placeholder="Your Email"
              onChange={handleChange}
            />
          </div>
          <div className="mb-3">
            <input
              className="inpts"
              name="password"
              type={passwordShown ? "text" : "password"}
              placeholder="Your Password"
              onChange={handleChange}
            />
            <i
              className={passwordShown ? " fa fa-eye " : "fa fa-eye-slash"}
              aria-hidden="true"
              id="togglePassword"
              onClick={togglePassword}
            ></i>
          </div>
          <div className="btnbtn">
            <button className="btn1">Submit</button>
          </div>
          {err && <p className="errror">{err}</p>}
        </form>
        <p>
          <a className="creat-link" href="#">
            {" "}
            Create an account?
          </a>
        </p>
      </div>
      <div className="about-wraper">
        <h6>About</h6>
        <h1>Evangadi Networks</h1>
        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat.
        </p>
        <p>
          Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do
          eiusmod tempor incididunt ut labore et dolore magna aliqua. Ut enim ad
          minim veniam, quis nostrud exercitation ullamco laboris nisi ut
          aliquip ex ea commodo consequat.
        </p>
        <button className="btn1">HOW IT WORKS</button>
      </div>
    </div>
  );
}

export default Login;
